head.script:
function getElementInfo() {
    var html = document.documentElement;
    var svg = document.getElementsByTagNameNS("http://www.w3.org/2000/svg", "svg")[0];

    alert(html.namespaceURI);
    alert(html.prefix);
    alert(html.localName);
    alert(html.tagName);

    alert(svg.namespaceURI);
    alert(svg.prefix);
    alert(svg.localName);
    alert(svg.tagName);

}
